mapper.select:
select

from driver


    and name = #{name,jdbcType=VARCHAR}



    and telephone = #{telephone,jdbcType=VARCHAR}



    and status = #{status,jdbcType=INTEGER}



    and auth_state = #{authState,jdbcType=INTEGER}



    and online_state = #{onlineState,jdbcType=INTEGER}



    and work_state = #{workState,jdbcType=INTEGER}



    and create_time >= CONCAT(#{createStartTime},' 00:00:00')



    and create_time <= CONCAT(#{createEndTime},' 23:59:59')


order by id
LIMIT #{offset},#{limit}

select

from driver
where name = #{name, jdbcType=VARCHAR}
