mapper.insert:

insert into user (id, username, password, 
  salt, name, sex, age, 
  telephone, status, type, 
  role_id, create_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{salt,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{sex,jdbcType=INTEGER}, #{age,jdbcType=INTEGER}, 
  #{telephone,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, 
  #{roleId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from user


    and username = #{username,jdbcType=VARCHAR}



    and password = #{password,jdbcType=VARCHAR}



    and salt = #{salt,jdbcType=VARCHAR}



    and name = #{name,jdbcType=VARCHAR}



    and sex = #{sex,jdbcType=INTEGER}



    and age = #{age,jdbcType=INTEGER}



    and telephone = #{telephone,jdbcType=VARCHAR}



    and status = #{status,jdbcType=INTEGER}



    and type = #{type,jdbcType=INTEGER}



    and role_id = #{roleId,jdbcType=INTEGER}



    and name = #{name,jdbcType=VARCHAR}



    and create_time >= CONCAT(#{createStartTime},' 00:00:00')



    and create_time <= CONCAT(#{createEndTime},' 23:59:59')


order by id
LIMIT #{offset},#{limit}
